import React from 'react';

const SearchBar = ({ search, setSearch }) => {

  return (
    <form style={{ display: 'flex', justifyContent: 'center' }} onSubmit={(e) => e.preventDefault()}>
        <input
            id='search'
            type='text'
            role='searchbox'
            placeholder='Search Books...'
            value={search}
            onChange={(e) => setSearch(e.target.value)}
            style={{ flexGrow: 1, borderRadius: '25rem 0 0 25rem' }}
        />
        <button type="submit" style={{ borderRadius: '0 25rem 25rem 0' }} className="searchButton">Search</button>
    </form> 
  );
};

export default SearchBar;
